```javascript
function extractAcclId(inputString) {
    // Regex pattern to match "Accl ID = " followed by letters, numbers and underscores
    const regexPattern = /Accl ID = [\w]+/g;

    // Matches all instances of the pattern in the input string
    const matches = inputString.match(regexPattern);

    // The output will contain the matched parts joined by the separator if more than one match is found
    return matches ? matches.join('') : '';
}

// Input string with anchor tag
const input = '<a href = "">Accl ID = TEST_TEST_1Accl ID = XXX</a>';

// Extracting the desired output
