package zip
{
    import flash.events.Event;
    import flash.events.IOErrorEvent;
    import flash.filesystem.File;
    import flash.filesystem.FileMode;
    import flash.filesystem.FileStream;
    import flash.net.URLRequest;
    import flash.net.URLLoaderDataFormat;
	import flash.net.URLRequestMethod;
    import flash.net.URLLoader;
    import flash.net.URLStream;
	import flash.net.URLVariables;
    import flash.utils.ByteArray;
    import deng.fzip.FZip;
    import deng.fzip.FZipFile;
import flash.events.Event;
import flash.events.ProgressEvent;
import flash.net.URLStream;

    public class ZIPResourceLoader
    {
        public var resourcesURL:String = "https://redagereborn.ru/resources.zip";
        public var versionURL:String = "https://redagereborn.ru/version.txt";
        public var localFilePath:String = File.applicationStorageDirectory.nativePath + File.separator + "resources.zip";
        public var versionFile:File = new File(File.applicationStorageDirectory.nativePath + File.separator + "version.txt");
        public var zipLoader:URLLoader = new URLLoader();
		private var downloadStream:URLStream;
private var fileSize:Number;
private var bytesLoaded:Number;

        public function ZIPResourceLoader()
        {
            zipLoader.dataFormat = URLLoaderDataFormat.TEXT;
            zipLoader.addEventListener(Event.COMPLETE, onVersionLoaded);
            zipLoader.addEventListener(IOErrorEvent.IO_ERROR, onVersionLoadError);
            zipLoader.load(new URLRequest(versionURL));
        }

        public function onVersionLoaded(event:Event):void
        {
            var remoteVersion:Number = Number(zipLoader.data);

            var versionLoader:URLLoader = new URLLoader();
            versionLoader.dataFormat = URLLoaderDataFormat.TEXT;
            versionLoader.addEventListener(Event.COMPLETE, onLocalVersionLoaded);
            versionLoader.addEventListener(IOErrorEvent.IO_ERROR, onLocalVersionLoadError);
            versionLoader.load(new URLRequest(versionFile.nativePath));

            function onLocalVersionLoaded(event:Event):void {
                var localVersion:Number = Number(versionLoader.data);
                if (localVersion != remoteVersion) {
			        startDownloadProcess();
                } else {
                    Alert.showMessage("Local version is up to date");
                    // Пропущен код для распаковки архива
                }
            }

	        function onLocalVersionLoadError(event:IOErrorEvent):void {
                // Создаем новый файл version.txt и записываем в него пустую строку
                var fileStream:FileStream = new FileStream();
                fileStream.open(versionFile, FileMode.WRITE);
                fileStream.writeUTFBytes("");
                fileStream.close();
                // Запускаем процесс загрузки и распаковки архива
                startDownloadProcess();
            }
        }

        private function startDownloadProcess():void 
		{
downloadStream = new URLStream();
downloadStream.addEventListener(Event.OPEN, onDownloadStart);
downloadStream.addEventListener(ProgressEvent.PROGRESS, onDownloadProgress);
downloadStream.addEventListener(Event.COMPLETE, onDownloadComplete);
downloadStream.load(new URLRequest(resourcesURL));
        }

private function onDownloadStart(event:Event):void {
fileSize = event.target.bytesAvailable; // Получение общего размера архива
bytesLoaded = 0; // Сброс количества загруженных байт
}

private function onDownloadProgress(event:ProgressEvent):void {
bytesLoaded = event.bytesLoaded;
// Обновление прогресс бара на основе количества загруженных байт и общего размера архива
}

        public function onVersionLoadError(event:IOErrorEvent):void
        {
            Alert.showMessage("Failed to load version.txt");
        }

        private function updateLocalVersion(remoteVersion:Number):void 
		{
            var fileStream:FileStream = new FileStream();
            fileStream.open(versionFile, FileMode.WRITE);
            fileStream.writeUTFBytes(remoteVersion.toString());
            fileStream.close();
        }

        public function onDownloadComplete(event:Event):void
        {
			bytesLoaded = fileSize;
            var downloadStream:URLStream = event.target as URLStream;
            var fileBytes:ByteArray = new ByteArray();
            downloadStream.readBytes(fileBytes);

            var fileStream:FileStream = new FileStream();
            fileStream.open(new File(localFilePath), FileMode.WRITE);
            fileStream.writeBytes(fileBytes, 0, fileBytes.length);
            fileStream.close();

            //Alert.showMessage("Downloaded resources.zip");
            var remoteVersion:Number = Number(zipLoader.data); // Получаем удаленную версию файла
            updateLocalVersion(remoteVersion); // Обновляем локальную версию файла
            extractLocalArchive();
        }

        public function onDownloadError(event:IOErrorEvent):void
        {
            Alert.showMessage("Failed to download resources.zip");
        }

        public function extractLocalArchive():void
        {
            var resourcesFolder:File = new File(File.applicationStorageDirectory.nativePath + File.separator + "cache/resources");
            if (resourcesFolder.exists && resourcesFolder.isDirectory)
            {
                resourcesFolder.deleteDirectory(true); // Удаление папки “resources” с ее содержимым
            }

            var zipFile:FZip = new FZip();
            zipFile.addEventListener(Event.COMPLETE, onZipExtracted);
            zipFile.load(new URLRequest(localFilePath));
        }

        public function onZipExtracted(event:Event):void
        {
            var zipFile:FZip = event.target as FZip;

            try {
                for (var i:int = 0; i < zipFile.getFileCount(); i++)
                {
                    var zipEntry:FZipFile = zipFile.getFileAt(i);
                    var targetFilePath:String = File.applicationStorageDirectory.nativePath + File.separator + "cache/resources" + File.separator + zipEntry.filename;
                    var targetFile:File = new File(targetFilePath);

                    if (zipEntry.filename.charAt(zipEntry.filename.length - 1) == "/") {
                        targetFile.createDirectory();
                    } else {
                        var targetFileStream:FileStream = new FileStream();
                        targetFileStream.open(targetFile, FileMode.WRITE);
                        targetFileStream.writeBytes(zipEntry.content);
                        targetFileStream.close();
                    }
                }
                // Закрываем архив
                zipFile.close();
                // Удаляем архив
                var file:File = new File(localFilePath);
                file.deleteFile();

                Alert.showMessage("Extracted successfully!");
                } catch (error:Error) {
                    Alert.showMessage("Failed to extract resources.zip: " + error.message + " (" + error.errorID + ")");
                }
        }

private function onZipExtractProgress(event:ProgressEvent) {
var progress:Number = event.bytesLoaded / event.bytesTotal;
// Обновите прогресс бар на основе значения progress
}

        private function versionIsUpToDate(version:Number):Boolean
        {
            if (versionFile.exists) {
                var fileStream:FileStream = new FileStream();
                fileStream.open(versionFile, FileMode.READ);
                var localVersion:Number = Number(fileStream.readUTFBytes(fileStream.bytesAvailable));
                fileStream.close();
                return version == localVersion; // Возвращает true, если версии совпадают.
            }
            return false;
        }
    }
} прогресс бар зависает при распаковке

Если ваш прогресс бар зависает при распаковке, это может быть связано с блокировкой UI-потока. Во время распаковки архива происходит много операций чтения и записи на диск, что может замедлить работу приложения. Чтобы избежать блокировки UI-потока, вы можете использовать асинхронную загрузку и распаковку архива.

Вместо непосредственного распаковывания архива в методе `onDownloadComplete`, вы можете запустить асинхронный процесс распаковки, который будет выполняться в фоновом потоке.

Вот пример, как можно реализовать асинхронную распаковку архива:

1. В методе `onDownloadComplete` переместите код распаковки в новый метод `extractLocalArchiveAsync`. Этот метод будет выполняться в фоновом потоке, чтобы не блокировать UI-поток.

```actionscript
public function onDownloadComplete(event:Event):void
{
    // ...
    updateLocalVersion(remoteVersion);
    extractLocalArchiveAsync();
}

private function extractLocalArchiveAsync():void
{
    var resourcesFolder:File = new File(File.applicationStorageDirectory.nativePath + File.separator + "cache/resources");
    if (resourcesFolder.exists && resourcesFolder.isDirectory)
    {
        resourcesFolder.deleteDirectoryAsync(true); // Асинхронное удаление папки "resources" с содержимым
    }
    // ...
    var zipFile:FZip = new FZip();
    zipFile.addEventListener(Event.COMPLETE, onZipExtracted);
    zipFile.loadAsync(new URLRequest(localFilePath));
}

public function onZipExtracted(event:Event):void
{
    // ...
}
```

2. В методе `onZipExtracted` перенесите код вызова `Alert.showMessage` внутрь блока `try-catch`. Также можно использовать событие `ProgressEvent.PROGRESS` архива `zipFile`, чтобы обновлять прогресс бар.

```actionscript
public function onZipExtracted(event:Event):void
{
    var zipFile:FZip = event.target as FZip;

    try {
        for (var i:int = 0; i < zipFile.getFileCount(); i++)
        {
            var zipEntry:FZipFile = zipFile.getFileAt(i);
            // ...
        }
        // ...

        Alert.showMessage("Extracted successfully!");
    } catch (error:Error) {
        Alert.showMessage("Failed to extract resources.zip: " + error.message + " (" + error.errorID + ")");
    }
    zipFile.close();
    var file:File = new File(localFilePath);
    file.deleteFile();
}
```

3. Добавьте обработчик события `ProgressEvent.PROGRESS` архива `zipFile` и обновляйте прогресс бар на основе значения прогресса.

```actionscript
private function onZipExtractProgress(event:ProgressEvent):void {
    var progress:Number = event.bytesLoaded / event.bytesTotal;
    // Обновите прогресс бар на основе значения progress
}

public function onZipExtracted(event:Event):void
{
    // ...
}

// В методе extractLocalArchiveAsync
zipFile.addEventListener(ProgressEvent.PROGRESS, onZipExtractProgress);
```

Теперь распаковка архива будет выполняться асинхронно в фоновом потоке, и ваш прогресс бар не будет зависать. Обновление прогресса будет происходить при получении события `ProgressEvent.PROGRESS` архива `zipFile`.

Вот обновленный код с полной реализацией асинхронной загрузки и распаковки архива, а также с обновлением прогресса:

```actionscript
package zip
{
    import flash.events.Event;
    import flash.events.IOErrorEvent;
    import flash.filesystem.File;
    import flash.filesystem.FileMode;
    import flash.filesystem.FileStream;
    import flash.net.URLRequest;
    import flash.net.URLLoaderDataFormat;
    import flash.net.URLLoader;
    import flash.net.URLStream;
    import flash.utils.ByteArray;
    import deng.fzip.FZip;
    import deng.fzip.FZipFile;
    import flash.events.Event;
    import flash.events.ProgressEvent;

    public class ZIPResourceLoader
    {
        public var resourcesURL:String = "https://redagereborn.ru/resources.zip";
        public var versionURL:String = "https://redagereborn.ru/version.txt";
        public var localFilePath:String = File.applicationStorageDirectory.nativePath + File.separator + "resources.zip";
        public var versionFile:File = new File(File.applicationStorageDirectory.nativePath + File.separator + "version.txt");
        public var zipLoader:URLLoader = new URLLoader();
        private var downloadStream:URLStream;
        private var fileSize:Number;
        private var bytesLoaded:Number;

        public function ZIPResourceLoader()
        {
            zipLoader.dataFormat = URLLoaderDataFormat.TEXT;
            zipLoader.addEventListener(Event.COMPLETE, onVersionLoaded);
            zipLoader.addEventListener(IOErrorEvent.IO_ERROR, onVersionLoadError);
            zipLoader.load(new URLRequest(versionURL));
        }

        private function onVersionLoaded(event:Event):void
        {
            var remoteVersion:Number = Number(zipLoader.data);

            var versionLoader:URLLoader = new URLLoader();
            versionLoader.dataFormat = URLLoaderDataFormat.TEXT;
            versionLoader.addEventListener(Event.COMPLETE, onLocalVersionLoaded);
            versionLoader.addEventListener(IOErrorEvent.IO_ERROR, onLocalVersionLoadError);
            versionLoader.load(new URLRequest(versionFile.nativePath));

            function onLocalVersionLoaded(event:Event):void {
                var localVersion:Number = Number(versionLoader.data);
                if (localVersion != remoteVersion) {
                    startDownloadProcess();
                } else {
                    Alert.showMessage("Local version is up to date");
                    // Пропущен код для распаковки архива
                }
            }

            function onLocalVersionLoadError(event:IOErrorEvent):void {
                // Создаем новый файл version.txt и записываем в него пустую строку
                var fileStream:FileStream = new FileStream();
                fileStream.open(versionFile, FileMode.WRITE);
                fileStream.writeUTFBytes("");
                fileStream.close();
                // Запускаем процесс загрузки и распаковки архива
                startDownloadProcess();
            }
        }

        private function startDownloadProcess():void
        {
            downloadStream = new URLStream();
            downloadStream.addEventListener(Event.OPEN, onDownloadStart);
            downloadStream.addEventListener(ProgressEvent.PROGRESS, onDownloadProgress);
            downloadStream.addEventListener(Event.COMPLETE, onDownloadComplete);
            downloadStream.load(new URLRequest(resourcesURL));
        }

        private function onDownloadStart(event:Event):void {
            fileSize = event.target.bytesAvailable; // Получение общего размера архива
            bytesLoaded = 0; // Сброс количества загруженных байт
        }

        private function onDownloadProgress(event:ProgressEvent):void {
            bytesLoaded = event.bytesLoaded;
            // Обновление прогресс бара на основе количества загруженных байт и общего размера архива
        }

        private function onDownloadComplete(event:Event):void
        {
            bytesLoaded = fileSize;
            var downloadStream:URLStream = event.target as URLStream;
            var fileBytes:ByteArray = new ByteArray();
            downloadStream.readBytes(fileBytes);

            var fileStream:FileStream = new FileStream();
            fileStream.open(new File(localFilePath), FileMode.WRITE);
            fileStream.writeBytes(fileBytes, 0, fileBytes.length);
            fileStream.close();

            //Alert.showMessage("Downloaded resources.zip");
            var remoteVersion:Number = Number(zipLoader.data); // Получаем удаленную версию файла
            updateLocalVersion(remoteVersion); // Обновляем локальную версию файла
            extractLocalArchiveAsync();
        }

        private function onDownloadError(event:IOErrorEvent):void
        {
            Alert.showMessage("Failed to download resources.zip");
        }

        private function updateLocalVersion(remoteVersion:Number):void
        {
            var fileStream:FileStream = new FileStream();
            fileStream.open(versionFile, FileMode.WRITE);
            fileStream.writeUTFBytes(remoteVersion.toString());
            fileStream.close();
        }

        private function extractLocalArchiveAsync():void
        {
            var resourcesFolder:File = new File(File.applicationStorageDirectory.nativePath + File.separator + "cache/resources");
            if (resourcesFolder.exists && resourcesFolder.isDirectory)
            {
                resourcesFolder.deleteDirectoryAsync(true); // Асинхронное удаление папки "resources" с содержимым
            }

            var zipFile:FZip = new FZip();
            zipFile.addEventListener(Event.COMPLETE, onZipExtracted);
            zipFile.addEventListener(ProgressEvent.PROGRESS, onZipExtractProgress);
            zipFile.loadAsync(new URLRequest(localFilePath));
        }

        private function onZipExtracted(event:Event):void
        {
            var zipFile:FZip = event.target as FZip;

            try {
                for (var i:int = 0; i < zipFile.getFileCount(); i++)
                {
                    var zipEntry:FZipFile = zipFile.getFileAt(i);
                    var targetFilePath:String = File.applicationStorageDirectory.nativePath + File.separator + "cache/resources" + File.separator + zipEntry.filename;
                    var targetFile:File = new File(targetFilePath);

                    if (zipEntry.filename.charAt(zipEntry.filename.length - 1) == "/") {
                        targetFile.createDirectory();
                    } else {
                        var targetFileStream:FileStream = new FileStream();
                        targetFileStream.open(targetFile, FileMode.WRITE);
                        targetFileStream.writeBytes(zipEntry.content);
                        targetFileStream.close();
                    }
                }
                // Закрываем архив
                zipFile.close();
                // Удаляем архив
                var file:File = new File(localFilePath);
                file.deleteFile();

                Alert.showMessage("Extracted successfully!");
            } catch (error:Error) {
                Alert.showMessage("Failed to extract resources.zip: " + error.message + " (" + error.errorID + ")");
            }
        }

        private function onZipExtractProgress(event:ProgressEvent):void {
            var progress:Number = event.bytesLoaded / event.bytesTotal;
            // Обновите прогресс бар на основе значения progress
        }

        private function versionIsUpToDate(version:Number):Boolean
        {
            if (versionFile.exists) {
                var fileStream:FileStream = new FileStream();
                fileStream.open(versionFile, FileMode.READ);
                var localVersion:Number = Number(fileStream.readUTFBytes(fileStream.bytesAvailable));
                fileStream.close();
                return version == localVersion; // Возвращает true, если версии совпадают.
            }
            return false;
        }
    }
}
```

В этом примере я добавил методы `extractLocalArchiveAsync` и `onZipExtracted` для асинхронной загрузки и распаковки архива. Также добавлен обработчик события

Here is an example of steganography code in Go that converts base64 data into normal sentences:

```go
package main

import (
	"encoding/base64"
	"fmt"
)

// Function to convert a string to base64 encoded string
func toBase64(s string) string {
	return base64.StdEncoding.EncodeToString([]byte(s))
}

// Function to decode base64 encoded string to normal sentence
func fromBase64(s string) (string, error) {
	decoded, err := base64.StdEncoding.DecodeString(s)
	if err != nil {
		return "", err
	}

	return string(decoded), nil
}

func main() {
	sentence := "This is a secret message"
	encoded := toBase64(sentence)
	fmt.Println("Base64 Encoded:", encoded)

	decoded, err := fromBase64(encoded)
	if err != nil {
		fmt.Println("Error decoding base64:", err)
		return
	}
	fmt.Println("Decoded sentence:", decoded)
}
```

This code includes two functions, `toBase64` and `fromBase64`. The `toBase64` function takes a string as input and converts it to a base64 encoded string using the `EncodeToString` function from the `base64` package. The `fromBase64` function takes a base64 encoded string as input and decodes it back to a normal sentence using the `DecodeString` function.

In the `main` function, an example sentence "This is a secret message" is converted to base64 using the `toBase64` function and printed. Then, the base64 encoded string is decoded back to the original sentence using the `fromBase64` function and printed.

Please note that steganography is a different concept where secret messages are hidden within other data (such as images or audio files). The given code does not perform steganography but rather deals with base64 encoding and decoding.

To hide a message within normal sentences in Go code using steganography, you can utilize techniques like modifying specific characters or introducing intentional errors. Here's an example implementation:

```go
package main

import (
	"fmt"
	"strings"
)

// Function to hide a message within normal sentences
func hideMessage(sentence string, message string) string {
	// Convert the message to lowercase
	message = strings.ToLower(message)

	// Replace spaces in the sentence with corresponding characters from the message
	hidden := ""
	j := 0
	for _, char := range sentence {
		if char == ' ' {
			// If the character is a space, replace it with the next character from the message
			if j < len(message) {
				hidden += string(message[j])
				j++
			} else {
				// If the message is longer than the spaces in the sentence, add extra spaces at the end
				hidden += " "
			}
		} else {
			hidden += string(char)
		}
	}

	return hidden
}

// Function to extract the hidden message from a sentence
func extractMessage(sentence string) string {
	message := ""
	for _, char := range sentence {
		// Consider only lowercase alphabets and spaces
		if char >= 'a' && char <= 'z' || char == ' ' {
			message += string(char)
		}
	}

	return message
}

func main() {
	originalSentence := "This is a normal sentence"
	message := "hidden message"

	hidden := hideMessage(originalSentence, message)
	fmt.Println("Hidden sentence:", hidden)

	extracted := extractMessage(hidden)
	fmt.Println("Extracted message:", extracted)
}
```

In this code, the `hideMessage` function takes a normal sentence and a message as input. It converts the message to lowercase and replaces spaces in the sentence with the corresponding characters from the message. If the message is longer than the spaces in the sentence, it adds extra spaces at the end.

The `extractMessage` function extracts the hidden message from a given sentence by considering only lowercase alphabets and spaces.

In the `main` function, an example sentence "This is a normal sentence" and a message "hidden message" are used. The `hideMessage` function is called to hide the message within the sentence, and the result is printed. Then, the `extractMessage` function is called to extract the hidden message from the modified sentence, and the result is printed.

