import {GPU} from "gpu.js";
export interface CanvasSize {
  width: number;
  height: number;
}

const OrderFeed = ({workerRef, symbol, settings}: OrderFeedProps) => {
  const cupParams = useSelector((state: AppState) => state.cupSlice);
  const [dpiScale, setDpiScale] = useState(Math.ceil(window.devicePixelRatio));
  const [canvasSize, setCanvasSize] = useState<CanvasSize>({height: 0, width: 0});
  const containerRef = useRef<HTMLDivElement|null>(null);
  const canvasRef = useRef<HTMLCanvasElement|null>(null);

  const gpu = new GPU();

  const draw = (trades: TradeItem[], camera: number) => {
    if (null === canvasRef || !Array.isArray(trades)) return;

    const context = canvasRef.current?.getContext("webgl");

    if (context) {
      const renderLine = gpu.createKernel(function(trades: TradeItem[], camera: number, aggregatedPriceStep: number, cupCellHeight: number, quantityDivider: number, minQuantity: number, dpiScale: number, theme: Theme, startX: number, fullHeightRowsCount: number, startY: number, halfCupCellHeight: number) {
        let startX = startX;
        const yModifier = (trade.price - (trade.price - (trade?.secondPrice || trade.price)) / 2 - camera) / aggregatedPriceStep * cupCellHeight + halfCupCellHeight;
        const width = 1.75 * clamp((trade.quantity / quantityDivider < minQuantity ? 0 : trade.radius), 5, 50) * dpiScale;

        this.color("black");
        this.strokeStart(startX - width / 2, startY - yModifier);
        startX = startX - width;
        this.strokeWidth(2 * dpiScale);
      }, {
        output: [canvasSize.width, canvasSize.height],
        graphical: true
      });

      const result = renderLine(trades, camera, cupParams.aggregatedPriceStep, cupParams.cellHeight, cupParams.quantityDivider, !settings.minQuantity ? 0 : parseFloat(settings.minQuantity), dpiScale, theme, canvasSize.width - 10 * dpiScale, parseInt((canvasSize.height / cupParams.cellHeight).toFixed(0)), (parseInt((canvasSize.height / cupParams.cellHeight).toFixed(0)) % 2 ? parseInt((canvasSize.height / cupParams.cellHeight).toFixed(0)) - 1 : parseInt((canvasSize.height / cupParams.cellHeight).toFixed(0)))) / 2 * cupParams.cellHeight;
    };

  return (
    <Box ref={containerRef} className=${styles.canvasWrapper} scroll-block>
      <canvas
        ref={canvasRef}
        className=${styles.canvas}
        width=${canvasSize.width}
        height=${canvasSize.height}
      />
    </Box>
  );
};

export default OrderFeed;

const clamp = (value: number, min: number, max: number): number => {
  return Math.min(Math.max(value, min), max);
};

