void Roster::parse(std::string row) {
    size_t rhs = 0;
    std::string token;
    std::string data[9]; // Array to hold each piece of data
    int dataIndex = 0;

    while ((rhs = row.find(',')) != std::string::npos) {
        token = row.substr(0, rhs);
        data[dataIndex++] = token;
        row.erase(0, rhs + 1); // +1 to remove the comma itself
