function [aligned_t, aligned_data] = align_samples(t, data, desired_fs)
    % Round the time to the nearest sample index using unique() and get the aligned data
    [~, aligned_indices] = unique(round(t*desired_fs), 'stable');
    aligned_t = t(aligned_indices);
    aligned_data = data(aligned_indices);
end

function [time, clean_data] = remove_nan_inf_outlier(t, data)
    % Remove NaNs and Infs
    not_nan_inf_indices = ~(isnan(data) | isinf(data));
    clean_data = data(not_nan_inf_indices);
    time = t(not_nan_inf_indices);

    % Remove the outliers using the IQR method
    q1 = quantile(clean_data, 0.25);
    q3 = quantile(clean_data, 0.75);
    iqr = q3 - q1;
    outlier_indices = (clean_data < (q1 - 1.5*iqr)) | (clean_data > (q3 + 1.5*iqr));
    time = time(~outlier_indices);
    clean_data = clean_data(~outlier_indices);
end
