use chrono::prelude::*;

fn get_current_date() -> String {
    let local: DateTime<Local> = Local::now();
    let month = local.format("%B").to_string();
    let day = local.day();
    let year = local.year();

    format!("{} {}, {}", month, day, year)
}

fn main() {
    println!("{}", get_current_date());
    // Output: "February 6, 2023"
}
```
