int binarySearch(int arr[], int n, int x)  
{
    // Verificar se o array não está vazio
    if (n == 0) 
        return ERROR;

    int left = 0, right = n - 1;

    // Encontrar o ponto médio em cada iteração
    while (left <= right) {  
       int mid = left + (right - left)/2; 

       // Verificar se o elemento está no meio
       if (arr[mid] == x) 
           return mid;  

       // Atualizar os índices esquerdo e direito
       if (arr[mid] < x)
           left = mid + 1;
       else
           right = mid - 1;
    }

    // Retornar valor de erro caso não seja encontrado    
    return ERROR;     
}
