    static double CalculatePi(int numberOfIterations)
    {
        double sum = 0;
        for (int i = 0; i < numberOfIterations; i++)
        {
            double r = random.NextDouble();
            double t = 2 * random.NextDouble() - 1;
            double u = Math.Sqrt(-2 * r * t) + 1;
            double v = Math.Sqrt(r * r + t * t);
            double value = (1 - (r * r) / (u * u)) * (1 - (t * t) / (v * v));
            sum += value;
        }
        return sum / numberOfIterations * 4;
    }
}
