let possibleYValues = [2, 3, 4];

// Loop through all possible values for x
for (let i = 0; i < possibleXValues.length; i++) {
  let xValue = possibleXValues[i];

  // Loop through all possible values for y, excluding the value that satisfies x + y = 5
  for (let j = 0; j < possibleYValues.length; j++) {
    let yValue = possibleYValues[j];
    if (yValue !== 2 && xValue + yValue !== 5) {
      // If the condition is satisfied, print the combination
      console.log(xValue + ", " + yValue);
