package com.uber.sdk.core.auth.internal;

import com.squareup.moshi.FromJson;
import com.squareup.moshi.ToJson;
import com.uber.sdk.core.auth.Scope;

import java.util.Set;

public class OAuthScopesAdapter {
    @FromJson
    @OAuthScopes
    Set<Scope> fromJson(String scopes) {
        return Scope.parseScopes(scopes);
    }

    @ToJson
    String toJson(@OAuthScopes Set<Scope> scopes) {
        return Scope.toStandardString(scopes);
    }
}
