import 'package:tsugenn_s_application1/core/app_export.dart'; // Assurez-vous que ce chemin correspond à votre structure de projet

class SignupTwoScreen extends StatefulWidget {
  const SignupTwoScreen({Key? key}) : super(key: key);

  @override
  SignupTwoScreenState createState() => SignupTwoScreenState();
}

class SignupTwoScreenState extends State<SignupTwoScreen> {
  final TextEditingController emailController = TextEditingController();
  final TextEditingController passwordController = TextEditingController();

  @override
  Widget build(BuildContext context) {
    return SafeArea(
      child: Scaffold(
        body: Container(
          width: double.maxFinite,
          padding: EdgeInsets.symmetric(horizontal: 26.h, vertical: 20.v),
          child: SingleChildScrollView( // Ajout pour gérer le clavier s'affichant
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                GestureDetector(
                  onTap: () => onTapImgArrowLeft(context),
                  child: CustomImageView(
                    imagePath: ImageConstant.imgArrowLeft,
                    width: 13.h,
                  ),
                ),
                SizedBox(height: 74.v),
                // La partie contenant l'image, "Compte Crée", et les champs de textes
                CustomImageView(
                  imagePath: ImageConstant.imgImage1,
                  height: 188.adaptSize,
                  width: 188.adaptSize,
                  margin: EdgeInsets.only(left: 66.h)
                ),
                SizedBox(height: 2.v),
                Align(
                  alignment: Alignment.center,
                  child: RichText(
                    text: TextSpan(
                      children: [
                        TextSpan(
                          text: "Compte ",
                          style: theme.textTheme.displaySmall.copyWith(color: Colors.black), // Assurez-vous que vos styles correspondent
                        ),
                        TextSpan(
                          text: "crée",
                          style: CustomTextStyles.displaySmallF53F5EFF, // Assurez-vous que vos styles correspondent
                        ),
                      ],
                    ),
                    textAlign: TextAlign.left,
                  )
                ),
                SizedBox(height: 20.v), // Ajout d'espace pour les champs de texte
                // Ajout des champs de texte pour l'email et le mot de passe
                TextField(
                  controller: emailController,
                  decoration: const InputDecoration(hintText: 'Email'),
                ),
                SizedBox(height: 16.v), // Espacement
                TextField(
                  controller: passwordController,
                  obscureText: true,
                  decoration: const InputDecoration(hintText: 'Mot de passe'),
                ),
                SizedBox(height: 20.v), // Espacement avant le bouton
                MaterialButton(
                  onPressed: signIn,
                  child: const Text('Sign In'),
                ),
                MaterialButton(
                  onPressed: () {
                    Navigator.pushNamed(context, '/signup'); // Assurez-vous que cette route est correcte
                  },
                  child: const Text('Sign Up'),
                ),
              ],
            ),
          ),
        ),
      ),
    );
  }

  Future<void> signIn() async {
    final ScaffoldMessengerState sm = ScaffoldMessenger.of(context);
    try {
      await SignIn.signInWithPassword(emailController.text, passwordController.text);
      sm.showSnackBar(const SnackBar(content: Text('Signed in')));
      Navigator.pushNamed(context, '/profile'); // Assurez-vous que cette route est correcte
    } on AuthException catch (e) {
      sm.showSnackBar(SnackBar(content: Text('Sign in failed: ${e.message}')));
    }
  }

  void onTapImgArrowLeft(BuildContext context) {
    Navigator.pop(context);
  }
}
