dodaj do tego programu jeszcze te funkcje: Ilość przeżytych dni, Znak Zodiaku, Znak Chiński/żywioł, Liczba Drogi Życia (obliczana jako sumą cyfr daty urodzenia)

package main

import (
	“fmt”
	“math”
	“time”
)

func main() {
	inputDate := getInputDate()

	birthday, err := parseDate(inputDate)
	if err != nil {
		fmt.Println(“Nieprawidłowy format daty.”)
		return
	}

	printBiorythms(“Dzień”, getDate(), calculateBiorythms(birthday, 23, 28, 33))
	printBiorythms(“Tydzień”, getDate(), calculateBiorythms(birthday, 237, 287, 337))
	printBiorythms(“Miesiąc”, getDate(), calculateBiorythms(birthday, 2330.4375, 2830.4375, 3330.4375))
	printBiorythms(“Rok”, getDate(), calculateBiorythms(birthday, 23365.25, 28365.25, 33*365.25))
}

func getInputDate() string {
	var inputDate string
	fmt.Print(“Wprowadź datę (RRRR-MM-DD): “)
	fmt.Scanln(&inputDate)
	return inputDate
}

func parseDate(inputDate string) (time.Time, error) {
	birthday, err := time.Parse(“2006-01-02”, inputDate)
	if err != nil {
		return time.Time{}, err
	}
	return birthday, nil
}

func getDate() string {
	now := time.Now()
	return now.Format(“02-01-2006”)
}

func calculateBiorythms(birthday time.Time, physicalCycle, emotionalCycle, intellectualCycle float64) (float64, float64, float64) {
	days := time.Since(birthday).Hours() / 24

	physical := math.Sin(2 * math.Pi * days / physicalCycle) * 100
	emotional := math.Sin(2 * math.Pi * days / emotionalCycle) * 100
	intellectual := math.Sin(2 * math.Pi * days / intellectualCycle) * 100

	return physical, emotional, intellectual
}

func printBiorythms(period, date string, biorythms []float64) {
	fmt.Println(”\nBiorytmy na”, period+“:”, date)
	fmt.Printf(“Fizyczny: %.2f%%\n”, biorythms[0])
	fmt.Printf(“Emocjonalny: %.2f%%\n”, biorythms[1])
	fmt.Printf(“Intelektualny: %.2f%%\n”, biorythms[2])
}

package main

import (
	"fmt"
	"math"
	"strings"
	"time"
)

func main() {
	inputDate := getInputDate()

	birthday, err := parseDate(inputDate)
	if err != nil {
		fmt.Println("Nieprawidłowy format daty.")
		return
	}

	printBiorythms("Dzień", getDate(), calculateBiorythms(birthday, 23, 28, 33))
	printBiorythms("Tydzień", getDate(), calculateBiorythms(birthday, 237, 287, 337))
	printBiorythms("Miesiąc", getDate(), calculateBiorythms(birthday, 2330.4375, 2830.4375, 3330.4375))
	printBiorythms("Rok", getDate(), calculateBiorythms(birthday, 23365.25, 28365.25, 33*365.25))

	printAdditionalInfo(birthday)
}

func getInputDate() string {
	var inputDate string
	fmt.Print("Wprowadź datę (RRRR-MM-DD): ")
	fmt.Scanln(&inputDate)
	return inputDate
}

func parseDate(inputDate string) (time.Time, error) {
	birthday, err := time.Parse("2006-01-02", inputDate)
	if err != nil {
		return time.Time{}, err
	}
	return birthday, nil
}

func getDate() string {
	now := time.Now()
	return now.Format("02-01-2006")
}

func calculateBiorythms(birthday time.Time, physicalCycle, emotionalCycle, intellectualCycle float64) (float64, float64, float64) {
	days := time.Since(birthday).Hours() / 24

	physical := math.Sin(2 * math.Pi * days / physicalCycle) * 100
	emotional := math.Sin(2 * math.Pi * days / emotionalCycle) * 100
	intellectual := math.Sin(2 * math.Pi * days / intellectualCycle) * 100

	return physical, emotional, intellectual
}

func printBiorythms(period, date string, biorythms []float64) {
	fmt.Println("\nBiorytmy na", period+":", date)
	fmt.Printf("Fizyczny: %.2f%%\n", biorythms[0])
	fmt.Printf("Emocjonalny: %.2f%%\n", biorythms[1])
	fmt.Printf("Intelektualny: %.2f%%\n", biorythms[2])
}

func printAdditionalInfo(birthday time.Time) {
	fmt.Println("\nDodatkowe informacje:")
	fmt.Println("Ilość przeżytych dni:", int(time.Since(birthday).Hours()/24))
	fmt.Println("Znak Zodiaku:", getZodiac(birthday.Month(), birthday.Day()))
	fmt.Println("Znak Chiński/żywioł:", getChineseZodiac(birthday.Year()))
	fmt.Println("Liczba Drogi Życia:", getLifePathNumber(birthday.Day(), birthday.Month(), birthday.Year()))
}

func getZodiac(month time.Month, day int) string {
	if (month == time.March && day >= 21) || (month == time.April && day <= 19) {
		return "Baran (Aries)"
	}
	if (month == time.April && day >= 20) || (month == time.May && day <= 20) {
		return "Byk (Taurus)"
	}
	if (month == time.May && day >= 21) || (month == time.June && day <= 20) {
		return "Bliźnięta (Gemini)"
	}
	if (month == time.June && day >= 21) || (month == time.July && day <= 22) {
		return "Rak (Cancer)"
	}
	if (month == time.July && day >= 23) || (month == time.August && day <= 22) {
		return "Lew (Leo)"
	}
	if (month == time.August && day >= 23) || (month == time.September && day <= 22) {
		return "Panna (Virgo)"
	}
	if (month == time.September && day >= 23) || (month == time.October && day <= 22) {
		return "Waga (Libra)"
	}
	if (month == time.October && day >= 23) || (month == time.November && day <= 21) {
		return "Skorpion (Scorpio)"
	}
	if (month == time.November && day >= 22) || (month == time.December && day <= 21) {
		return "Strzelec (Sagittarius)"
	}
	if (month == time.December && day >= 22) || (month == time.January && day <= 19) {
		return "Koziorożec (Capricorn)"
	}
	if (month == time.January && day >= 20) || (month == time.February && day <= 18) {
		return "Wodnik (Aquarius)"
	}
	return "Ryby (Pisces)"
}

func getChineseZodiac(year int) string {
	animals := []string{"Szczur", "Bawół", "Tygrys", "Królik", "Smok", "Wąż", "Koń", "Koza", "Małpa", "Kogut", "Pies", "Świnia"}
	element := []string{"drewno", "ogień", "ziemia", "metal", "woda"}
	animalIndex := (year - 4) % len(animals)
	elementIndex := (year/2 - 4) % len(element)

	return fmt.Sprintf("%s %s", element[elementIndex], animals[animalIndex])
}

func getLifePathNumber(day, month, year int) int {
	date := fmt.Sprintf("%02d%02d%d", day, month, year)
	sum := 0
	for _, digit := range date {
		sum += int(digit - '0')
	}

	return sum
}

Aby dodać generowanie wykresu i zapisywanie go do pliku PNG, możemy użyć pakietu "github.com/wcharczuk/go-chart".

Najpierw dodajmy import tego pakietu:

```go
import (
	// inne importy...
	"os"
	"strings"

	chart "github.com/wcharczuk/go-chart"
)
```

Następnie dodajmy funkcję createChart(), która będzie generować wykres:

```go
func createChart(birthday time.Time, physicalCycle, emotionalCycle, intellectualCycle float64) {
	numberOfDays := int(time.Since(birthday).Hours() / 24)

	physicalData := make([]chart.Value, numberOfDays)
	emotionalData := make([]chart.Value, numberOfDays)
	intellectualData := make([]chart.Value, numberOfDays)

	for i := 0; i < numberOfDays; i++ {
		days := float64(i)
		physicalData[i] = chart.Value{
			Label: fmt.Sprintf("%d", i),
			Value: math.Sin(2*math.Pi*days/physicalCycle) * 100,
		}
		emotionalData[i] = chart.Value{
			Label: fmt.Sprintf("%d", i),
			Value: math.Sin(2*math.Pi*days/emotionalCycle) * 100,
		}
		intellectualData[i] = chart.Value{
			Label: fmt.Sprintf("%d", i),
			Value: math.Sin(2*math.Pi*days/intellectualCycle) * 100,
		}
	}

	series := []chart.ChartSeries{
		{ // Physical
			Name:    "Fizyczny",
			XValues: chart.IndexValueFormatter(physicalData),
			YValues: chart.ValueFormatter(physicalData),
		},
		{ // Emotional
			Name:    "Emocjonalny",
			XValues: chart.IndexValueFormatter(emotionalData),
			YValues: chart.ValueFormatter(emotionalData),
		},
		{ // Intellectual
			Name:    "Intelektualny",
			XValues: chart.IndexValueFormatter(intellectualData),
			YValues: chart.ValueFormatter(intellectualData),
		},
	}

	graph := chart.Chart{
		Title:      "Wykres biorytmów",
		TitleStyle: chart.StyleShow(),

		XAxis: chart.XAxis{
			Name:      "Dni",
			NameStyle: chart.StyleShow(),
			Style:     chart.StyleShow(),
			TickStyle: chart.StyleShow(),
		},

		YAxis: chart.YAxis{
			Name:      "Wartość",
			NameStyle: chart.StyleShow(),
			Style:     chart.StyleShow(),
			TickStyle: chart.StyleShow(),
		},

		Background: chart.Style{
			Padding: chart.Box{
				Top:    20,
				Left:   20,
				Right:  20,
				Bottom: 20,
			},
		},

		Series: series,
	}

	f, err := os.Create("biorythms.png")
	if err != nil {
		fmt.Println("Błąd przy zapisie pliku.")
		return
	}
	defer f.Close()

	err = graph.Render(chart.PNG, f)
	if err != nil {
		fmt.Println("Błąd przy generowaniu wykresu.")
		return
	}

	fmt.Println("Wykres został zapisany jako: biorythms.png")
}
```

Teraz, w funkcji main(), po wywołaniu printAdditionalInfo(birthday), możemy dodać wywołanie createChart():

```go
printAdditionalInfo(birthday)
createChart(birthday, 23, 28, 33)
```

To spowoduje wygenerowanie wykresu biorytmów i zapisanie go do pliku "biorythms.png".

Pamiętaj, aby dodać import do pakietu "github.com/wcharczuk/go-chart" oraz zainstalować ten pakiet za pomocą polecenia "go get github.com/wcharczuk/go-chart" przed uruchomieniem programu.
